mapper.select:
SELECT *
FROM players
ORDER BY status DESC;

mapper.update:
UPDATE players
SET status = 1 # 1=>playing
WHERE id = #{id};

UPDATE players
SET status = 0 # 0=>offline
WHERE id = #{id};

UPDATE players
SET status = 2 # 2=>available
WHERE id = #{id};

UPDATE players
SET best_score = #{bestScore}
WHERE id = #{id}

UPDATE players
SET host_name = #{hostName}
WHERE id = #{id}
